import React, { useState, useEffect } from 'react';
import AddDirectorsForm from './components/AddDirectorsForm';
import './style.css';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom'; // Step 1: Import Link

function App() {
  const [directors, setDirectors] = useState([]);
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    // FetchDirectors
    fetch('https://localhost:7125/api/Directors')
      .then((response) => response.json())
      .then((data) => {
        setDirectors(data);
      })
      .catch((error) => {
        console.error('Error fetching Directors:', error);
      });

    // Fetch Movies
    fetch('https://localhost:7125/api/Movie')
      .then((response) => response.json())
      .then((data) => {
        setMovies(data);
        console.log(data);
      })
      .catch((error) => {
        console.error('Error fetching movies from database: ', error);
      });
  }, []);

  return (
    // Step 2: Wrap your content with Router
    <Router>
      <div className="App">
        <header className="App-header">
          <h1>Movies nga databaza:</h1>
          <ul>
            {movies.map((movie) => (
              <li key={movie.movieId}>
                {movie.title} i lansuar ne vitin {movie.releaseYear} i krijuar nga {movie.director.name}
              </li>
            ))}
          </ul>

          <h1>Directors nga databaza</h1>
          <ul>
            {directors.map((director) => (
              <li key={director.directorId}>
                {director.name} i lindur me date {director.birthYear}
              </li>
            ))}
          </ul>

          {/* Step 3: Create a Link to AddDirectorsForm */}
          <Link to="/add-directors">Add Directors</Link>
        </header>
      </div>

      {/* Step 3: Create a Route for AddDirectorsForm */}
      <Route path="/add-directors" component={AddDirectorsForm} />
    </Router>
  );
}

export default App;
