import React, { useState } from 'react';

function AddDirectorsForm() {
  const [name, setDirectorsName] = useState('');
  const [birthYear, setBirthYear] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    const newAuthor = {
      name: name,
      birthYear: birthYear,
    };

    fetch('https://localhost:7125/api/Directors', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newAuthor),
    })
      .then((response) => {
        if (response.ok) {
          return response.json();
        } else {
          console.log('Failed to fetch the author data');
        }
      })
      .then((data) => {
        console.log('Author saved successfully', data);
      })
      .catch((error) => {
        console.log('Author cannot be saved in the database', error);
      });
  }

  return (
    <div>
      <h1>Add Directors Form</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="text"
            value={name}
            onChange={(e) => setDirectorsName(e.target.value)}
            required
            placeholder="Director's Name"
          />
        </div>
        <div>
          <input
            type="number"
            value={birthYear}
            onChange={(e) => setBirthYear(e.target.value)}
            required
            placeholder="Director's Birth Year"
          />
        </div>
        <div>
          <button type="Submit" className="butoni">
            <b>Shto</b>
          </button>
        </div>
      </form>
    </div>
  );
}

export default AddDirectorsForm;
