import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';
import AddDirectorsForm from './components/AddDirectorsForm';
import './style.css';
import { BrowserRouter as Router, Link, Route, Routes } from 'react-router-dom'; // Import Routes

function App() {
  const [directors, setDirectors] = useState([]);
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    //FetchDirectors
    fetch("https://localhost:7125/api/Directors")
      .then(response => response.json())
      .then(data => {
        setDirectors(data);
      })
      .catch(error => {
        console.error("Error fetching Directors:", error);
      });

    //Fetch Movies
    fetch("https://localhost:7125/api/Movie")
      .then(response => response.json())
      .then(data => {
        setMovies(data);
        console.log(data);
      })
      .catch(error => {
        console.error("Error fetching movies from database: ", error);
      });

  }, []);

  return (
    <Router>
      <div className="App">
        <p>test</p>
        <Routes>
          <Route path="/" element={
            <>
              <header className="App-header">
                <h1>Movies nga databaza:</h1>
                <ul>
                  {movies.map(movie => (
                    <li key={movie.movieId}>
                      {movie.title} i lansuar ne vitin {movie.releaseYear} i krijuar nga {movie.director.name}
                    </li>
                  ))}
                </ul>
                <Link to="/add-directors">Add Directors</Link>
                <h1>Directors nga databaza</h1>
                <ul>
                  {directors.map(director => (
                    <li key={director.directorId}>
                      {director.name} i lindur me date {director.birthYear}
                    </li>
                  ))}
                </ul>
              </header>
            </>
          } />
          <Route path="/add-directors" element={<AddDirectorsForm />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
